#include<bits/stdc++.h>using namespace std; typedef long long int ll; #define endl '\n'#define vll vector<ll>#define vi vector<int>#define pll pair<ll,ll>#define pi pair<int,int>#define pb push_back#define fr(i,a,b) for(ll i=a;i<(ll)b;i++)#define rfr(i,a,b) for(ll i=a-1;i>=(ll)b;i--) const int N = 1e6+1;const ll mod=1e9+7;bool prime[N]; #define MAXN ll(1e6+1) // stores smallest prime factor for every numberint spf[MAXN]; // Calculating SPF (Smallest Prime Factor) for every// number till MAXN.// Time Complexity : O(nloglogn)void sieve(){    spf[1] = 1;    for (int i=2; i<MAXN; i++)        spf[i] = i;     for (int i=4; i<MAXN; i+=2)        spf[i] = 2;     for (int i=3; i*i<MAXN; i++)    {        if (spf[i]==i)        {            for (int j=i*i; j<MAXN; j+=i)                if(spf[j]==j)                    spf[j]=i;        }    }} vector<bool> is_prime(N+1, true);void sieve(int n){	is_prime[0] = is_prime[1] = false; 	for (int i = 2; i <= n; i++) {	    if (is_prime[i] && (long long)i * i <= n) {	        for (int j = i * i; j <= n; j += i)	            is_prime[j] = false;	    }	} } int main(){    std::ios_base::sync_with_stdio(false); std::cin.tie(NULL); std::cout.tie(NULL);    #ifndef ONLINE_JUDGE    freopen("input.txt","r",stdin);    freopen("output1.txt","w",stdout);    #endif     int t=1;    // cin>>t;    while(t--)    {    	int n; cin>>n;    	int m; cin>>m;    	int q; cin>>q;    	vll v(n);    	fr(i,0,n)    	{    		cin>>v[i];    	}    	// sort(v.rbegin(), v.rend());    	    	vector<pll> ind; vll val;    	fr(i,0,m)    	{    		int x,y,z; cin>>x>>y>>z;    		ind.pb({x-1,y-1});val.pb(z);    	}     	vll cnt(m+1,0);    	fr(i,0,q)    	{    		int l,r; cin>>l>>r;l--,r--;    		cnt[l]+=1;    		if(r+1<=m)    		cnt[r+1]-=1;    	}     	fr(i,1,m+1)    	{    		cnt[i]+=cnt[i-1];    	}     	ll dp[n+1]={0};    	fr(i,0,m)    	{    		dp[ind[i].first]+=val[i]*cnt[i];    		if(ind[i].second+1<=n)    		{dp[ind[i].second+1]-=val[i]*cnt[i];}    	}    	fr(i,1,n+1)    	{    		dp[i]+=dp[i-1];    	}    	fr(i,0,n)    	{    		cout<<(v[i]+dp[i])<<" ";    	}     }     #ifndef ONLINE_JUDGE    cout<<"\nTime Elapsed: " << 1.0*clock() / CLOCKS_PER_SEC << " sec\n";    #endif        return 0;}
