import java.util.*;import java.math.*;public class YaroslavPermutation{	public static void main(String[] args)		{			Scanner sc = new Scanner(System.in);			int size = sc.nextInt();			int temp = size;			int[] arr = new int[1001];			while(temp-->0)			{				arr[sc.nextInt()]++;			}			Arrays.sort(arr);			if(arr[1000]==1)				System.out.println("YES");			else			{				for(int i = 0;i<1001;i++)				{					if(arr[i]>((size+1)/2))					{						System.out.println("NO");						return;					}				}				System.out.println("YES");			}		}}
