import java.util.*;public class Main{		 public static void main(String args[])    {        Scanner in  = new Scanner(System.in);      int n = in.nextInt();     int a[] = new int[n];     for(int i=0 ; i<n ; i++) a[i]=in.nextInt();     int flag=0;     for(int i=0 ; i<n ; i++)     {         for(int j=i+1 ; j<n ; j++)         {             for(int k=0 ; k<n ; k++)             {                if(k!=i&&k!=j)                {                    if(a[k]==(a[i]+a[j]))                    {                        flag=1;                        System.out.println((k+1)+" "+(i+1)+" "+(j+1));                        break;                    }                }             }             if(flag==1) break;         }         if(flag==1) break;     }     if(flag==0) System.out.println("-1");    }}
