# link: https://codeforces.com/problemset/problem/313/Bif __name__ == "__main__":    s = input()    s = list(s)    s = s[::-1]    n = len(s)    prefix_sum = [0 for i in range(n)]    for i in range(n-1):        if s[i] == s[i+1]:            prefix_sum[i] = 1    prefix_sum = prefix_sum[::-1]            for i in range(1,n):        prefix_sum[i] += prefix_sum[i-1]    # Now I have a prefix sum    m = int(input())    while m:        l,r = map(int,input().split())        print(prefix_sum[r-1] - prefix_sum[l-1])        m -= 1             
