//package codeforces;import java.util.*; public class Reconnaissance { 	public static void main(String[] args) {		Scanner scan = new Scanner(System.in); 		int N = scan.nextInt(); 		int D = scan.nextInt(); 		int[] A = new int[N];		scan.nextLine(); 		for(int i = 0; i < N; i++) {			A[i] = scan.nextInt();		}		//System.out.println(Arrays.toString(A));		//Arrays.sort(A);				// 6 10		// 4 6 4 1 9 3		int count = 0; 				/*		for(int i = 0; i < N-1; i++) {			if(A[i+1] - A[i] <= D) {				count++; 			}		}		*/		for(int i = 0; i < N; i++) {			int h1 = A[i];			for(int j = 0; j < N; j++) {				if(i != j) {					int h2 = A[j];					if(Math.abs(h1 - h2) <= D) {						count++; 					}				}			}		}		System.out.println(count);	} }
