import java.util.Scanner;  public class A { 	/**	 * @param args	 */	public static void main(String[] args) {		BWCube bwc = new BWCube();		bwc.print();	} } class BWCube {		BWCube () {		Scanner in = new Scanner(System.in);		n = in.nextInt();			}		boolean sol () {		if (n % 2 != 0) {			return false;		}		return true;	}		void formLayers() {		layer_o = new char[n][n];		layer_e = new char[n][n];				int lo = n/2-1;		int hi = n/2;				int rnd = 0;				while (lo >= 0) {			for (int k = lo; k <= hi; k++){				layer_o[lo][k] = layer_o[hi][k] = bw[rnd];				layer_e[lo][k] = layer_e[hi][k] = bw[1-rnd];			}					for (int k = lo+1; k < hi; k++){				layer_o[k][lo] = layer_o[k][hi] = bw[rnd];				layer_e[k][lo] = layer_e[k][hi] = bw[1-rnd];			}						lo--;			hi++;			rnd = 1-rnd;		}				lro = new String[n];		lre = new String[n];				for (int i = 0; i < n; i++){			lro[i] = String.valueOf(layer_o[i]);			lre[i] = String.valueOf(layer_e[i]);		}			}	void printLayers() { 		int lr = 0;				while (lr < n) { 			for (int i = 0; i < n; i++){				System.out.println(lro[i]);			}			System.out.println(); 			for (int i = 0; i < n; i++){				System.out.println(lre[i]);			}			System.out.println();						lr = lr + 2;		}	}		void print() {		if (sol()) {			formLayers();			printLayers();		}		else {			System.out.println("-1");		}	} 	char[][] layer_o;	char[][] layer_e;		String[] lro;	String[] lre;		int n;	static char[] bw = {'b', 'w'};}
