//solution referenced by mj101import java.util.*;public class Main{    public static void main(String gg[]) {        Scanner sc = new Scanner(System.in);        int n = sc.nextInt();        int nr = 0;        int nc = 0;        int [] row = new int[n];        int [] col = new int[n];        for(int i = 0; i<n; i++){            String temp = sc.next();            for(int j = 0; j<n; j++){                if(temp.charAt(j) == '.'){                    if(col[j] == 0){                        col[j] = i+1;                        nc++;                    }                    if(row[i] == 0){                        row[i] = j+1;                        nr++;                    }                }            }        }        if(nr==n){            for(int i=0;i<n;i++){                System.out.println((i+1)+" "+row[i]);            }        }        else if(nc==n){            for(int i=0;i<n;i++){                System.out.println(col[i]+" "+(i+1));            }        }        else{            System.out.println("-1");        }    }}
