// practice with rainboyimport java.io.*;import java.util.*; public class CF329A extends PrintWriter {	CF329A() { super(System.out); }	Scanner sc = new Scanner(System.in);	public static void main(String[] $) {		CF329A o = new CF329A(); o.main(); o.flush();	} 	void main() {		int n = sc.nextInt();		int[] rr = new int[n];		int[] cc = new int[n];		Arrays.fill(rr, -1);		Arrays.fill(cc, -1);		for (int i = 0; i < n; i++) {			byte[] aa = sc.next().getBytes();			for (int j = 0; j < n; j++)				if (aa[j] == '.') {					rr[i] = j;					cc[j] = i;				}		}		boolean yes = true;		for (int i = 0; i < n; i++)			if (rr[i] == -1) {				yes = false;				break;			}		if (yes) {			for (int i = 0; i < n; i++)				println((i + 1) + " " + (rr[i] + 1));			return;		}		yes = true;		for (int j = 0; j < n; j++)			if (cc[j] == -1) {				yes = false;				break;			}		if (yes) {			for (int j = 0; j < n; j++)				println((cc[j] + 1) + " " + (j + 1));			return;		}		println(-1);	}}
