//package com.company; import java.util.Arrays;import java.util.Scanner; public class Codeforces {    static int INF = 1_000_000000;     private static void sport(String s1, String s2, int[][] dist) {        if (s1.length() != s2.length()) {            System.out.println(-1);            return;        }        int n = s1.length();        for (int k = 0; k < 26; k++) {            for (int i = 0; i < 26; i++) {                for (int j = 0; j < 26; j++) {                    dist[i][j] = Math.min(dist[i][j], dist[i][k] + dist[k][j]);                }            }        }        int ans = 0;        StringBuilder builder = new StringBuilder();        for (int i = 0; i < n; i++) {            int first = s1.charAt(i) - 'a';            int second = s2.charAt(i) - 'a';            if (first == second) {                builder.append(s1.charAt(i));                continue;            }            int val = INF;            int curr = 0;            for (int j = 0; j < 26; j++) {                int temp = dist[first][j] + dist[second][j];                if (temp < val) {                    val = temp;                    curr = j;                }            }            if (val == INF && dist[first][second] == INF && dist[second][first] == INF) {                System.out.println(-1);                return;            }            if (val < dist[first][second] && val < dist[second][first]) {                builder.append((char) ('a' + curr));                ans += val;                continue;            }            if (dist[first][second] > dist[second][first]) {                builder.append(s1.charAt(i));                ans += dist[second][first];            } else {                builder.append(s2.charAt(i));                ans += dist[first][second];            }        }        System.out.println(ans);        System.out.println(builder.toString());    }     public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        String s1 = sc.next();        String s2 = sc.next();        int n = sc.nextInt();        int[][] cost = new int[26][26];        for (int[] ints : cost) {            Arrays.fill(ints, INF);        }        for (int i = 0; i < n; i++) {            int from = sc.next().charAt(0) - 'a';            int to = sc.next().charAt(0) - 'a';            cost[from][to] = Math.min(sc.nextInt(), cost[from][to]);        }        sport(s1, s2, cost);    }}
