import java.util.Scanner; public class cakeminator { 	public static void main(String args[]) {		Scanner s = new Scanner(System.in);		int r =s.nextInt();		int c= s.nextInt();		int total = r*c;		boolean [] row = new boolean[r];		boolean [] col = new boolean[c];		s.nextLine();		int monsC=0;		int monsR=0;		int k=0;		while(k<r) {			String p =s.nextLine();			for(int i=0;i<p.length();i++) {				if(p.charAt(i)=='S') {										if(!row[k]) {					monsR++;					row[k]=true;					}					if(!col[i]) {						monsC++;						col[i]=true;					}				}			}			k++;		}		if(monsC*monsR>=total) {			System.out.println(0);		}		else {		System.out.println(total-monsC*monsR);		}		}}
