//package codeforces; import java.util.Scanner; public class CapitalSmall {		public static void main(String[] args) {		Scanner sc = new Scanner(System.in);		int n = sc.nextInt();		int m = sc.nextInt();		char[][] cake = new char[n][m];		sc.nextLine();				for(int i = 0; i < n; i++) {			cake[i]= sc.nextLine().toCharArray();		}		sc.close();				int count = 0;		boolean flag = false;				for(int i = 0; i < n; i++) {			flag = false;			for(int j = 0; j < m; j++) {				if(cake[i][j] == 'S') {					flag = true;					break;				}			}			if(flag == false) {				for(int j = 0; j < m; j++) {					if(cake[i][j] != '1') {						count++;						cake[i][j] = '1';					}										}			}		}				for(int i = 0; i < m; i++) {			flag = false;			for(int j = 0; j < n; j++) {				if(cake[j][i] == 'S') {					flag = true;					break;				}			}			if(flag == false) {				for(int j = 0; j < n; j++) {					if(cake[j][i] != '1') {						count++;						cake[j][i] = '1';					}										}							}		}		System.out.println(count);	}}
