// practice with rainboyimport java.io.*;import java.util.*; public class CF333B extends PrintWriter {	CF333B() { super(System.out, true); }	Scanner sc = new Scanner(System.in);	public static void main(String[] $) {		CF333B o = new CF333B(); o.main(); o.flush();	} 	void main() {		int n = sc.nextInt();		int m = sc.nextInt();		boolean[] rr = new boolean[n];		boolean[] cc = new boolean[n];		while (m-- > 0) {			int i = sc.nextInt() - 1;			int j = sc.nextInt() - 1;			rr[i] = cc[j] = true;		}		int cnt = 0;		for (int i = 1; i < n - 1; i++) {			if (!rr[i])				cnt++;			if (!cc[i])				cnt++;		}		if (n % 2 == 1 && !rr[n / 2] && !cc[n / 2])			cnt--;		println(cnt);	}}
