import java.util.*;public class Solution{    public static void main(String[] args)    {        Scanner sc = new Scanner(System.in);        int students = sc.nextInt();        int puzzles = sc.nextInt();        int size[] = new int[puzzles];        for(int i=0;i<puzzles;i++)        {            size[i] = sc.nextInt();        }        Arrays.sort(size);        int ans = Integer.MAX_VALUE;        for(int i=0;i<=puzzles-students;i++)        {            int diff = size[i+students-1]-size[i];            if(diff<ans)            {                ans = diff;            }        }        System.out.println(ans);    }}
