//package codeforces;import java.util.*;public class puzzles { 	//problem 337A Puzzles		public static void main(String[] args) {				Scanner s = new Scanner(System.in);				int numStudents = s.nextInt();		int numPuzzles = s.nextInt();		int[] puzzles = new int[numPuzzles+1];				for(int i = 1; i <= numPuzzles; i++) {			puzzles[i] = s.nextInt();		}				Arrays.sort(puzzles);		//		for(int q = 0; q < puzzles.length; q++)System.out.print(puzzles[q] + " ");//		System.out.println();				int numDifferences = (numPuzzles-numStudents)+1;		int[] differences = new int[numDifferences];				for(int j = 0; j < numDifferences; j++) {			int endIndex = numPuzzles-j; //i.e. 6-0 --> 6			int beginIndex = (endIndex-numStudents)+1; //i.e. (6-4)+1 --> 3 , 													 //so the first range would be puzzles 3 thru 6			//			System.out.println("endIndex = " + endIndex);//			System.out.println("beginIndex = " + beginIndex);			differences[j] = puzzles[endIndex] - puzzles[beginIndex];		}				Arrays.sort(differences);		System.out.println(differences[0]); 	} }/*4 610 12 10 7 5 22*/
