import java.util.*;public class RoutineProblem {    static class Fraction{        int n;        int d;        Fraction(int n, int d){            this.n=n;            this.d=d;        }        Fraction(int n){            this.n=n;            this.d=1;        }        public Fraction dif(Fraction y){            Fraction x=this;            int num=(x.n * y.d)-(y.n * x.d);            int den=x.d*y.d;            for(int i=Math.min(num, den);i>=2;i--){                if(den%i==0 && num%i==0){                    den/=i;                    num/=i;                }            }            if(num==0)                den=1;            Fraction ans=new Fraction(num,den);            return ans;        }        public Fraction mul(Fraction y){            Fraction x=this;            int num=x.n*y.n;            int den=x.d*y.d;            for(int i=Math.min(num, den);i>=2;i--){                if(den%i==0 && num%i==0){                    den/=i;                    num/=i;                }            }            if(num==0)                den=1;            Fraction ans=new Fraction(num,den);            return ans;        }        public Fraction div(Fraction y){            Fraction x=this;            int num=x.n * y.d;            int den=x.d*y.n;            for(int i=Math.min(num, den);i>=2;i--){                if(den%i==0 && num%i==0){                    den/=i;                    num/=i;                }            }            if(num==0)                den=1;            Fraction ans=new Fraction(num,den);            return ans;        }    }    public static void main(String args[]){        Scanner sc= new Scanner(System.in);        int a=sc.nextInt();        int b=sc.nextInt();        int c=sc.nextInt();        int d=sc.nextInt();        Fraction A=new Fraction(a);        Fraction B=new Fraction(b);        Fraction C=new Fraction(c);        Fraction D=new Fraction(d);                Fraction ev=A.div(C);        ev= D.mul(ev);        ev=B.dif(ev);        ev=ev.div(B);        if((ev.n<0&&ev.d>0)||(ev.n>0&&ev.d<0))            ev=new Fraction(Integer.MAX_VALUE);                Fraction eh=B.div(D);        eh= C.mul(eh);        eh=A.dif(eh);        eh=eh.div(A);        if((eh.n<0&&eh.d>0)||(eh.n>0&&eh.d<0))            eh=new Fraction(Integer.MAX_VALUE);                if(ev.n/ev.d < eh.n/eh.d)            System.out.println(ev.n+"/"+ev.d);        else            System.out.println(eh.n+"/"+eh.d);    }}
