import java.util.Scanner;public class HelpfulMaths {    public static void main(String args[]) {      Scanner  in = new Scanner(System.in);      String input = in.nextLine();      int arr[] = new int[10];      String output = "";      if (input.contains("+")){      for (int i = 0; i < input.length(); i++){          int index = input.charAt(i) - 48;          arr[index]++;          i++;      }      for (int i = 0; i < arr.length; i++){          if (arr[i] > 0){              while(arr[i] != 0){                  String si = String.valueOf(i);                  if (output.isEmpty())                    output += si;                else                    output += "+" + si;                  arr[i]--;              }          }      }      }      else      output = input;      System.out.print(output);    }}
