// 34B - Sale// https://codeforces.com/problemset/problem/34/B import java.util.Arrays;import java.util.Scanner; public class CF34B {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int n = sc.nextInt();        int m = sc.nextInt();        int[] arr = new int[n];        for (int i = 0; i < n; i++) {            arr[i] = sc.nextInt();        }        Arrays.sort(arr);        int sum = 0;        for (int i = 0; i < m; i++) {            if (arr[i] >= 0) {                break;            }            sum += arr[i];        }        System.out.println(-sum);        sc.close();    }}
