import java.io.*;import java.util.*;public class Main{    public static void main(String args[]){        Scanner sc=new Scanner(System.in);	    int n=sc.nextInt();	    String[] arr=new String[n];	    for(int i=0;i<n;i++){	        arr[i]=sc.next();	    }	    int cnt=1;	    for(int i=0;i<n-1;i++){	        if(!(arr[i].equals(arr[i+1]))){	            cnt++;	        }	    }	    System.out.println(cnt);    }}
