//package Coding; import java.io.*;import java.util.*; public class CodeForces {    public static void main(String[] args) throws IOException {        Scanner in = new Scanner(System.in);        int n = in.nextInt();        int fixed = 0;        int[] p = new int[n];        boolean hasPair = false;        for (int i = 0; i < n; ++i) {            p[i] = in.nextInt();        }         for (int i = 0; i < n; ++i) {            if (p[i] == i) {                fixed++;            } else {                if (p[p[i]] == i) {                    hasPair = true;                }            }        }        if (hasPair) {            fixed += 2;        } else {            if (fixed < n) {                fixed++;            }        }         System.out.println(fixed);     }  }          
