//189301019.akshayimport java.io.BufferedReader; import java.io.IOException; import java.io.InputStreamReader; import java.util.Scanner; import java.util.Random;import java.util.Arrays;import java.util.StringTokenizer; import java.util.ArrayList;import java.util.HashMap;import java.util.HashSet;import java.util.Collections; public class B {     public static void main(String[] args) 	{ 		FastReader sc=new FastReader(); 		StringBuffer ans=new StringBuffer();		int test=1;		while(test-->0)		{			int n=sc.nextInt();			int arr[]=new int[10];			int maxm =0;int maxi=0;			for(int i=1;i<=9;i++) {				arr[i]=sc.nextInt();				if(n/arr[i] > maxm) {					maxm =n/arr[i];					maxi =i;				}			}						if(maxi == 0) {				ans.append(-1+"\n");				continue;			}						int tt=maxm;			StringBuffer res=new StringBuffer();			String x=Integer.toString(maxi);			while(tt>0) {				res.append(x);				--tt;			}			char num[] =res.toString().toCharArray();						int rem =n%arr[maxi];						inner:for(int i=0;i<num.length;i++) {				int available =arr[maxi] + rem;				for(int j=9;j>=maxi;j--) {					if(arr[j]<=available) {						num[i]=Integer.toString(j).charAt(0);						rem =available%arr[j];						continue inner;					}				}			}						ans.append(new String(num)+"\n");										}			    System.out.print(ans);	}             static final Random random=new Random();    	static void ruffleSort(int[] a) {		int n=a.length;//shuffle, then sort 		for (int i=0; i<n; i++) {			int oi=random.nextInt(n), temp=a[oi];			a[oi]=a[i]; a[i]=temp;		}		Arrays.sort(a);	}		static void ruffleSort(long[] a) {		int n=a.length;//shuffle, then sort 		for (int i=0; i<n; i++) {			int oi=random.nextInt(n);			long temp=a[oi];			a[oi]=a[i]; a[i]=temp;		}		Arrays.sort(a);	}		static class FastReader 	{ 		BufferedReader br; 		StringTokenizer st;  		public FastReader() 		{ 			br = new BufferedReader(new					InputStreamReader(System.in)); 		}  		String next() 		{ 			while (st == null || !st.hasMoreElements()) 			{ 				try				{ 					st = new StringTokenizer(br.readLine()); 				} 				catch (IOException e) 				{ 					e.printStackTrace(); 				} 			} 			return st.nextToken(); 		}  		int nextInt() 		{ 			return Integer.parseInt(next()); 		}  		long nextLong() 		{ 			return Long.parseLong(next()); 		}  		double nextDouble() 		{ 			return Double.parseDouble(next()); 		}  		String nextLine() 		{ 			String str = ""; 			try			{ 				str = br.readLine(); 			} 			catch (IOException e) 			{ 				e.printStackTrace(); 			} 			return str; 		} 	}   } 
