# https://codeforces.com/contest/35/problem/A fo = open("output.txt", "w") with open("input.txt") as fin:    i = 0    ball = [0, 0, 0, 0]    n = 0    for line in fin:        if i == 0:            n = int(line)            ball[n] = 1            i += 1        else:            x1, x2 = list(map(int, line.split()))            ball[x1], ball[x2] = ball[x2], ball[x1]            i += 1            if i == 4:                for x in range(1, 4):                    if ball[x] == 1:                        fo.write(str(x) + "\n")                i = 0                ball = [0, 0, 0, 0]                n = 0         
