#include <bits/stdc++.h>#define pb push_back#define speed ios_base::sync_with_stdio(false); cin.tie(0); cout.tie(0)using namespace std;typedef long long ll;typedef long double ld;typedef vector <ll> vll;typedef vector <vll> vvll; const ll N=2005;const ld inf=20000.0;const ll MOD=1e9+7;ld a[2*N],dp[2*N][N];ll n,vis[2*N][N]; ld solve(ll i,ll upLeft){    //Base condition    ll downLeft=2*n-i-upLeft+1;    if(upLeft<0 || downLeft<0)        return inf;    if(i>2*n)        return 0;    //Check if already calculated    ld &ans=dp[i][upLeft];    if(vis[i][upLeft])        return ans;    vis[i][upLeft]=1;    ans=inf;        //Recursive solution    ld sol1=(ceil(a[i])-a[i])+solve(i+1,upLeft-1);    ld sol2=(floor(a[i])-a[i])+solve(i+1,upLeft);    if(abs(sol1)<abs(sol2))        return ans=sol1;    else        return ans=sol2;} int main(){     speed;    cin>>n;    for(ll i=1;i<=2*n;i++)        cin>>a[i];    memset(dp,-1,sizeof(dp));    cout<<fixed<<setprecision(3)<<abs(solve(1,n));    return 0;}
