//package Coding; import java.io.*;import java.math.BigInteger;import java.nio.Buffer;import java.util.*; public class CodeForces {    public static void main(String[] args) throws FileNotFoundException {        Scanner in = new Scanner(System.in);        int k = in.nextInt();        int d = in.nextInt();        if (d == 0) {            if (k == 1) {                System.out.println(0);            } else {                System.out.println("No solution");            }        } else {            String ans = String.valueOf(d);            for (int i = 0; i < k - 1; ++i) {                ans += "0";            }            System.out.println(ans);        }          }  }          
