import java.io.File;import java.io.FileNotFoundException;import java.io.PrintWriter;import java.util.*;public class Main{	public static void main(String[] args) throws FileNotFoundException {				Scanner sc = new Scanner(new File("input.txt"));		PrintWriter pw = new PrintWriter("output.txt");		int n = sc.nextInt();		String signals = sc.next();		ArrayList<Integer> onesPos = new ArrayList<>();		for(int i = 0; i < n; i ++){			if(signals.charAt(i) == '1')				onesPos.add(i);		}		int diff = onesPos.get(1) - onesPos.get(0);		for(int i = 2; i < onesPos.size(); i++){			if(diff != onesPos.get(i) - onesPos.get(i-1)){				pw.println("NO");				pw.close();				return;			}						}		pw.println("YES");		pw.close();	}} 	     						 	 	 				 	   	
