import java.util.Arrays;import java.util.Collections;import java.util.Scanner;import java.lang.Math; public class Account {			public static void main (String[] args) {		Scanner sc= new Scanner(System.in) ;		int n= sc.nextInt() ;		int m= sc.nextInt() ;		int[] dirty= new int[m] ;		for(int i=0 ;i<m ;i++ ) {  			dirty[i]= sc.nextInt() ;		}		Arrays.sort(dirty);		if(m == 0) {			System.out.println("YES");			return ;		}		if(dirty[0]==1  ||  dirty[m-1]==n) {						System.out.println("NO");			return ;		}		for(int i=0 ;i< m-2 ;i++ ) {			if(dirty[i]+1 == dirty[i+1]) {				if(dirty[i+1]+1 == dirty[i+2]) {					System.out.println("NO");					return ;				}			}		}		System.out.println("YES"); 	}		}
