import java.util.*;public class Test {  public static void main(String[] args) {      Scanner in = new Scanner(System.in);      int n = in.nextInt();      int k = in.nextInt();       String[] a = new String[n];      int coun =0;        for(int ind=0;ind<n;ind++){        a[ind]=in.next();        }        for(int i=0;i<a.length;i++){         String st = a[i];         int j;         for(j=48;j<=k+48;j++){           if(!st.contains(""+((char)j)))               break;         }         if(j==(k+49)){         coun++;         }        }        System.out.println(coun);  }} 		  		  	   			 		  	   				 	
