import java.util.ArrayList;import java.util.Arrays;import java.util.Collections;import java.util.Scanner;import java.lang.Math; public class Account {			public static void main (String[] args) {				Scanner sc= new Scanner(System.in) ;		int n= sc.nextInt() ;		int k= sc.nextInt() ;		int[] sum= new int[k] ;		int min= Integer.MAX_VALUE ,res= 0 ;		for(int i=0 ;i<n ;i++ ) {			sum[i%k] += sc.nextInt() ;		}for(int i=0 ;i<k ;i++ ) {			if(sum[i] < min) {				res= i ;				min= sum[i] ;			}		}		System.out.println(res + 1);	}	}
