import java.util.*;public class DimaAndToDoList {    public static void main(String args[]){        Scanner sc= new Scanner(System.in);        int n=sc.nextInt();        int k=sc.nextInt();        int a[]=new int[n];        for(int i=0;i<n;i++)            a[i]=sc.nextInt();        long b[]=new long[k];        for(int i=0;i<n;i++)            b[i%k]+=a[i];        long min=Long.MAX_VALUE;        int in=0;        for(int i=0;i<k;i++){            if(b[i]<min){                min=b[i];                in=i;            }        }        System.out.println(in+1);    }}
