# link: https://codeforces.com/problemset/problem/368/Bif __name__ == "__main__":    n,m = map(int, input().split())    a = list(map(int, input().split()))    suffix_sum = [0 for i in range(n)]    seen = {}    for i in range(n-1,-1,-1):        if a[i] not in seen:            suffix_sum[i] += 1            seen[a[i]] = True    for i in range(n-1,-1,-1):        if i==n-1:            continue        suffix_sum[i] += suffix_sum[i+1]    while m:        print(suffix_sum[int(input())-1])        m-= 1               
