# https://codeforces.com/problemset/problem/369/A def plates(n, m, k, arr):    count = 0    for i in range(n):        if arr[i] == 1:            if m:                m -= 1            else:                count += 1        if arr[i] == 2:            if k:                k -= 1            elif m:                m -= 1            else:                count += 1     return count  n, m, k = map(int, input().split())arr = list(map(int, input().split()))print(plates(n, m, k, arr))
