import java.util.*;public class Main{		 public static void main(String args[])    {        Scanner in  = new Scanner(System.in);        int n = in.nextInt();         int m = in.nextInt();        int k = in.nextInt();        int a[] = new int[n];        int c1=0 , c2=0;        int ans=0;       for(int i=0 ; i<n ; i++)        {            a[i] = in.nextInt();            if(a[i]==1) c1++;            else c2++;        }        m-=c1;        if(m<0)          ans+=Math.abs(m);        c2=(m>0)?c2-m:c2;        k-=c2;        if(k<0)         ans+=Math.abs(k);         System.out.println(ans);           }}
