// practice with rainboyimport java.io.*;import java.util.*; public class CF37B extends PrintWriter {	CF37B() { super(System.out); }	Scanner sc = new Scanner(System.in);	public static void main(String[] $) {		CF37B o = new CF37B(); o.main(); o.flush();	} 	void main() {		int n = sc.nextInt();		int max = sc.nextInt();		int reg = sc.nextInt();		int[] pow = new int[n];		int[] dmg = new int[n];		int damage = 0;		for (int i = 0; i < n; i++) {			pow[i] = sc.nextInt();			dmg[i] = sc.nextInt();			if (pow[i] == 100)				damage += dmg[i];		}		if (damage <= reg) {			println("NO");			return;		}		boolean[] used = new boolean[n];		int[] tt = new int[n];		int[] ii = new int[n];		int t = 0, k = 0;		int health = max;		damage = 0;		while (health > 0) {			int i_ = -1;			for (int i = 0; i < n; i++)				if (!used[i] && pow[i] * max >= health * 100 && (i_ == -1 || dmg[i_] < dmg[i]))					i_ = i;			if (i_ != -1) {				used[i_] = true;				damage += dmg[i_];				tt[k] = t;				ii[k] = i_;				k++;			}			t++;			health -= damage;			health += reg;			if (health > max)				health = max;		}		println("YES");		println(t + " " + k);		for (int h = 0; h < k; h++)			println(tt[h] + " " + (ii[h] + 1));	}}
