import java.util.*;public class asd{    static Scanner s=new Scanner(System.in);            public static void main(String args[])    {        int t=1;        while(t-->0)        {            solve();        }    }    static void solve()    {        int a=s.nextInt();int b=s.nextInt();int c=s.nextInt();int d=s.nextInt();        int diff1=Math.abs(a-c);int sum1=a+b;        int sum2=c+d;         int diff2=Math.abs(b-d);        if(a==c&&b==d)            System.out.print("0 ");        else if(a==c||b==d)            System.out.print("1 ");        else            System.out.print("2 ");        if(sum1%2==sum2%2)        {            if(diff1==diff2)            {                System.out.print("1 ");            }            else            {                System.out.print("2 ");            }        }        else            System.out.print("0 ");        System.out.print(Math.max(diff1,diff2));    }}
