//package CodeForces; import java.util.ArrayList;import java.util.Collections;import java.util.Comparator;import java.util.HashMap;import java.util.Scanner; public class BerlandBingo { 	public static void main(String[] args) {		// TODO Auto-generated method stub		Scanner s=new Scanner(System.in);		int n=s.nextInt();		ArrayList<ArrayList<Integer>> arr=new ArrayList<ArrayList<Integer>>();		for(int i=0;i<n;i++)		{			ArrayList<Integer> temp=new ArrayList<Integer>();			int no=s.nextInt();			for(int j=0;j<no;j++)			{				int small=s.nextInt();				temp.add(small);			}			Collections.sort(temp);			arr.add(temp);		}		int ans[]=new int[n];		for(int i=0;i<n;i++)		{			ans[i]=-1;		}		for(int i=0;i<n;i++)		{			for(int j=0;j<n;j++)			{				if(i!=j)				{					ArrayList<Integer> a=arr.get(i);					ArrayList<Integer> b=arr.get(j);					int q=0;int r=0;int count=0;					while(q<a.size()&&r<b.size())					{						if(a.get(q)<b.get(r))						{							q++;						}						else if(a.get(q)>b.get(r))						{							r++;						}						else						{							count++;							q++;							r++;						}					}										if(a.size()==count)					{						ans[j]=0;					}					if(count==b.size())					{						ans[i]=0;					}				}			}		}				for(int i=0;i<n;i++)		{			if(ans[i]==0)			{				System.out.println("NO");			}			else			{				System.out.println("YES");			}		}		 	} }
