import java.util.*;public class Main{    public static void main(String[] args)    {        Scanner in=new Scanner(System.in);        int n=in.nextInt();        HashSet<Integer>[] ary=new HashSet[n];        for(int i=0;i<n;i++)        {            int m=in.nextInt();            HashSet<Integer> cards=new HashSet<Integer>();            for(int j=0;j<m;j++) cards.add(in.nextInt());            ary[i]=cards;        }        for(int i=0;i<n;i++)        {            boolean flag=true;            HashSet<Integer> source=ary[i];            for(int j=0;j<n;j++)            {                if(i==j) continue;                HashSet<Integer> target=ary[j];                boolean containsEverything=true;                for(int k:target)                {                    if(!source.contains(k))                    {                        containsEverything=false;                        break;                    }                }                if(containsEverything)                {                    flag=false;                    break;                }                            }            System.out.println(flag?"YES":"NO");        }    }            }
