import java.io.*;import java.util.*;public class Main{	public static void main(String[] args)throws Exception	{	    BufferedReader br=new BufferedReader(new InputStreamReader(System.in));	    String s=br.readLine();	    String str[]=s.split(" ");	    int n=Integer.parseInt(str[0]);	    int m=Integer.parseInt(str[1]);	    int i=Integer.parseInt(str[2]);	    int j=Integer.parseInt(str[3]);	    int a=Integer.parseInt(str[4]);	    int b=Integer.parseInt(str[5]);	    int ar1[]={1,n,n,1};	    int ar2[]={m,1,m,1};	    int min=-1;	    for(int l=0;l<4;l++)	    {	        int temp1=Math.abs(ar1[l]-i);	        int temp2=Math.abs(ar2[l]-j);	        if(temp1%a!=0||temp2%b!=0)	        continue;	        temp1/=a;	        temp2/=b;	        if(temp1%2!=temp2%2)	        continue;	        int d=Math.max(temp1,temp2);	        if(min==-1||d<min)	        min=d;	    }	    if(a+i>n&&i-a<=0&&min!=0||b+j>m&&j-b<=0&&min!=0)	    System.out.println("Poor Inna and pony!");	    else if(min==-1)		System.out.println("Poor Inna and pony!");		else		System.out.println(min);	}}
