import java.io.*;public class p374B {    public static void main(String[] args) throws IOException {        BufferedReader in=new BufferedReader(new InputStreamReader(System.in));        BufferedWriter out = new BufferedWriter(new OutputStreamWriter(System.out));        char s[]=in.readLine().toCharArray();        long c=1,a=1;        for(int i=1;i<s.length;i++)            if(s[i]+s[i-1]=='i') c++;            else{if(c%2==1)a*=c/2+1;c=1;}        if(c%2==1)a*=(c/2+1);        out.write(a+"");out.flush();    }}
