# ------------------- fast io --------------------import osimport sysfrom io import BytesIO, IOBase BUFSIZE = 8192  class FastIO(IOBase):    newlines = 0     def __init__(self, file):        self._fd = file.fileno()        self.buffer = BytesIO()        self.writable = "x" in file.mode or "r" not in file.mode        self.write = self.buffer.write if self.writable else None     def read(self):        while True:            b = os.read(self._fd, max(os.fstat(self._fd).st_size, BUFSIZE))            if not b:                break            ptr = self.buffer.tell()            self.buffer.seek(0, 2), self.buffer.write(b), self.buffer.seek(ptr)        self.newlines = 0        return self.buffer.read()     def readline(self):        while self.newlines == 0:            b = os.read(self._fd, max(os.fstat(self._fd).st_size, BUFSIZE))            self.newlines = b.count(b"\n") + (not b)            ptr = self.buffer.tell()            self.buffer.seek(0, 2), self.buffer.write(b), self.buffer.seek(ptr)        self.newlines -= 1        return self.buffer.readline()     def flush(self):        if self.writable:            os.write(self._fd, self.buffer.getvalue())            self.buffer.truncate(0), self.buffer.seek(0)  class IOWrapper(IOBase):    def __init__(self, file):        self.buffer = FastIO(file)        self.flush = self.buffer.flush        self.writable = self.buffer.writable        self.write = lambda s: self.buffer.write(s.encode("ascii"))        self.read = lambda: self.buffer.read().decode("ascii")        self.readline = lambda: self.buffer.readline().decode("ascii")  sys.stdin, sys.stdout = IOWrapper(sys.stdin), IOWrapper(sys.stdout)input = lambda: sys.stdin.readline().rstrip("\r\n") # ------------------- fast io --------------------from math import gcd, ceil def prod(a, mod=10**9+7):    ans = 1    for each in a:        ans = (ans * each) % mod    return ans def lcm(a, b): return a * b // gcd(a, b) def binary(x, length=16):    y = bin(x)[2:]    return y if len(y) >= length else "0" * (length - len(y)) + y def check_div(s):    ans = 0    j = 0    for i in range(len(s)-1, -1, -1):        ans += s[i]*pow(10,j,7)        ans %= 7        j += 1    return ans % 7 for _ in range(int(input()) if not True else 1):    #n = int(input())    #n, k = map(int, input().split())    #a, b = map(int, input().split())    #c, d = map(int, input().split())    #a = list(map(int, input().split()))    #b = list(map(int, input().split()))    s = [int(k) for k in input()]    a,b,c,d=0,0,0,0    zero=0    s2=[]    for i in s:        if i == 0:            zero += 1        elif i==1 and not a:            a+=1        elif i == 6 and not b:            b+=1        elif i == 8 and not c:            c+=1        elif i == 9 and not d:            d += 1        else:            s2 += [i]    s22 = check_div(s2)    fk = [[1, 6, 8, 9], [1, 6, 9, 8], [1, 8, 6, 9], [1, 8, 9, 6], [1, 9, 6, 8], [1, 9, 8, 6], [6, 1, 8, 9], [6, 1, 9, 8], [6, 8, 1, 9], [6, 8, 9, 1], [6, 9, 1, 8], [6, 9, 8, 1], [8, 1, 6, 9], [8, 1, 9, 6], [8, 6, 1, 9], [8, 6, 9, 1], [8, 9, 1, 6], [8, 9, 6, 1], [9, 1, 6, 8], [9, 1, 8, 6], [9, 6, 1, 8], [9, 6, 8, 1], [9, 8, 1, 6], [9, 8, 6, 1]]    for f in fk:        if (s22*(10**4) + check_div(f))%7==0:            print("".join(str(k) for k in s2+f+[0]*zero))            quit()
