import java.util.*;import java.io.*;import java.util.regex.*; public class Solution {	public static void main(String[] args) throws Exception {		Scanner scan = new Scanner(System.in);		String s = scan.nextLine();		long rPlace = s.indexOf("^");		long rSum = 0;		long lSum = 0;		boolean leftSide = true;		for(int i = 0; i < s.length(); i++) {			char ch = s.charAt(i);			if(ch == '^') {				leftSide = false;			} else 			if(ch != '=') {				long shoulder;				int w = ch - '0';				if(leftSide) {					shoulder = rPlace - i;					lSum += shoulder*w;				} else {					shoulder = i - rPlace;					rSum += shoulder*w;				}			}		}				String result = (lSum == rSum) ? "balance" : (lSum > rSum) ? "left" : "right";				System.out.println(result);	}	}
