import java.util.*; public class Solution { 	public static void main(String[] args) {		Scanner sc = new Scanner(System.in);		int a=sc.nextInt();                int b=sc.nextInt();                int win=0;                int draw=0;                int loss=0;                for(int i=1;i<=6;i++){                    if(Math.abs(a-i)<Math.abs(b-i))                        win++;                    else if(Math.abs(a-i)==Math.abs(b-i))                        draw++;                    else                        loss++;                }                System.out.println(win+" "+draw+" "+loss);	}}
