//package codeforces; import java.util.Scanner; public class CapitalSmall {		public static void main(String[] args) {		Scanner sc = new Scanner(System.in);		int a = sc.nextInt();		int b = sc.nextInt();		sc.close();				int one = 0, draw = 0, two = 0;				for(int i = 1; i <= 6; i++) {			int adiff = Math.abs(a - i);			int bdiff = Math.abs(b - i); 						if(adiff < bdiff) {				one++;			}						else if(adiff == bdiff) {				draw++;			}						else {				two++;			}		}				System.out.println(one + " " + draw + " " + two);	}}
