import java.util.*;import java.io.*;import java.lang.*;public class Code{    public static void main (String[] args){        Scanner sc=new Scanner(System.in);        int n=sc.nextInt();        int[]arr=new int[n];        for(int i=0;i<arr.length;i++){            arr[i]=sc.nextInt();        }        int i=0;        int j=arr.length-1;        boolean flag=true;        int s=0;        int d=0;        while(i<=j){            if(flag==true){                if(arr[i]>arr[j])                {                    s=s+arr[i];                    flag=false;                    i++;                }                else{                    s=s+arr[j];                    flag=false;                    j--;                }            }            else{                if(arr[i]>arr[j])                {                    d=d+arr[i];                    flag=true;                    i++;                }                else{                    d=d+arr[j];                    flag=true;                    j--;                }            }        }        System.out.println(s+" "+d);    }}
