//package codeforces; import java.util.Scanner; public class CapitalSmall {		public static void main(String[] args) {		Scanner sc = new Scanner(System.in);		int n = sc.nextInt();		int c = sc.nextInt();		int[] prices = new int[n]; 		for(int i = 0; i < n; i++) {			prices[i] = sc.nextInt();		}				sc.close();				int maxProfit = 0;		for(int i = 0; i < prices.length - 1; i++) {			int profit = prices[i] - prices[i + 1] - c;			maxProfit = Math.max(maxProfit, profit);		}				System.out.println(maxProfit);	}}
