import java.io.ByteArrayInputStream;import java.io.File;import java.io.PrintWriter;import java.util.Scanner;import static java.lang.Math.sqrt;public class A {  int USE_FILE = 0;  int HAS_TESTCASES = 0;  String INPUT = "4";  void solve(int caseNr) {    long n = in.nextInt();    long x = (int) sqrt(n * n / 2);    if (n == 0) {      System.out.println(1);      return;    }    if (n == 1) {      System.out.println(4);      return;    }    if ((x + 1) * (x + 1) + x * x <= n * n) {      System.out.println(8 * x + 4);    } else {      System.out.println(8 * x);    }  }  static boolean LOCAL = java.lang.System.getProperty("ONLINE_JUDGE") == null;  static Scanner in;  static PrintWriter output_writer = new PrintWriter(java.lang.System.out);  static class System {    static PrintWriter out = null;  }  void setup() throws Exception {    System.out = output_writer;    if (LOCAL && USE_FILE == 0) {      java.lang.System.setIn(new ByteArrayInputStream(INPUT.getBytes()));      in = new Scanner(java.lang.System.in);    }    if (LOCAL && USE_FILE > 0) {      String filename = String.format("in/cf%d.txt", USE_FILE);      File inputFile = new File(filename);      in = new Scanner(inputFile);      System.out.printf("[%s]\n", filename);    }    if (!LOCAL) {      in = new Scanner(java.lang.System.in);    }    long startTime = java.lang.System.currentTimeMillis();    int t = HAS_TESTCASES > 0 ? in.nextInt() : 1;    for (int i = 1; i <= t; i++) {      solve(i);    }    in.close();    if (LOCAL) {      System.out.printf("[%dms]\n", java.lang.System.currentTimeMillis() - startTime);    }    output_writer.flush();  }  public static void main(String[] args) throws Exception {    new A().setup();  }}
