import java.lang.Math; import java.io.*;public class HelloWorld{    public static void main(String[] args) throws IOException  {   BufferedReader bf=new BufferedReader(new InputStreamReader(System.in));   int n=Integer.parseInt(bf.readLine());   PrintWriter pw=new PrintWriter(new BufferedWriter(new OutputStreamWriter(System.out)));   pw.write((int)Math.max(1,4*(int)(Math.sqrt(2)*n))+"\n");   pw.flush();   }}
