import java.util.Scanner;public class Main {   public static void main(String[] args)   {       Scanner input = new Scanner(System.in);       String s = input.next();       char x='|',y='+',z='=';       long a=0,b=0,c=0,p=0,e=0;       int i;       for(i=0;i<s.length();i++)       {           if(s.charAt(i)==y) {p++;}           else if(s.charAt(i)==z) {e++;}           else if( s.charAt(i)== x && p==0 &&e==0) {a++;}           else if( s.charAt(i)== x && p>0 &&e==0) {b++;}           else if( s.charAt(i)== x && p>0 &&e>0) {c++;}       }       if(a+b==c)       {           System.out.println(s);       }       else if(Math.abs((a+b)-c)==2)       {           if(a+b>c)            {                if(a>b && a>1) a--;                else b--;                c++;            }           else{ a++;c--;}           for(i=0;i<a;i++) System.out.print("|");           System.out.print("+");           for(i=0;i<b;i++) System.out.print("|");           System.out.print("=");           for(i=0;i<c;i++) System.out.print("|");           System.out.print("\n");                        }       else       {          System.out.println("Impossible");       }   }}
