# url: https://codeforces.com/contest/40/problem/a# tag:# difficulty:from typing import List INF = float("inf")NINF = float("-inf")  def read_string():    return input()  def read_string_line():    return [x for x in input().split(" ")]  def read_int_line():    return [int(x) for x in input().split(" ")]  def read_int():    return int(input())  def get_int_arr(s):    return [int(x) for x in s.split()]  def get_sum(acc, l, r):    """     l, r are original index     """    return acc[r] - acc[l]  P = int(1e9 + 7)  def exgcd(x, y):    if y == 0:        return x, 1, 0    g, a, b = exgcd(y, x % y)    t = x // y    return g, b, a - t * b  def inv(x):    g, a, b = exgcd(x, P)    return a  def calc_acc(arr):    ans = [0]    for a in arr:        ans.append(a + ans[-1])    return ans  class Point:    def __init__(self, x, y):        self.x = x        self.y = y     def __sub__(self, other):        return Point(self.x - other.x, self.y - other.y)     def dot(self, other):        return self.x * other.x + self.y * other.y     def cross(self, other):        return self.x * other.y - self.y * other.x     def __str__(self):        return "Point(%s, %s)" % (self.x, self.y)     def abs(self):        return self.abs2() ** 0.5     def abs2(self):        return self.dot(self)  def get_point(line):    x, y = get_int_arr(line)    return Point(x, y)  eps = 1e-8lines: List[str] = [*open(0)]pt = get_point(lines[0])r = pt.abs()if pt.x == 0 or pt.y == 0 or abs(r - int(r)) <= eps or abs(r - int(r + 0.5)) <= eps:    print("black")else:    r = int(r)    if (r % 2 == 0 and pt.x * pt.y > 0) or (r % 2 == 1 and pt.x * pt.y < 0):        print("black")    else:        print("white")
