import java.util.Scanner;  public class A { 	public static void main(String[] args) {		Scanner in = new Scanner(System.in);		int n = in.nextInt();				String board[] = new String[n];		for(int i=0; i<n; i++) {			board[i] = in.next();		}				boolean ok = true;				char l1 = board[0].charAt(0);		char l2 = board[0].charAt(1);				if(l1 == l2) {			ok = false;		}				for(int i=0; i<n; i++) {			for(int j=0; j<n; j++) {				if (i==j || i == (n-j-1)) {					if(board[i].charAt(j) != l1) {						ok = false;					}				} else {					if(board[i].charAt(j) != l2) {						ok = false;					}				}			}		}				System.out.println(ok ? "YES" : "NO");	} }
