import java.util.*;// scanner.nextInt();public class Solution {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);                   		int n = scanner.nextInt();                int m = scanner.nextInt();                int k = scanner.nextInt();                                int[][] chats = new int[n][m];                		//  String[] arr = {"a","b","c", "d","e", "f" , "g" , "h", "i" , "j" , "k" , "l", "m", "n", "o", "p", "q" , "r", "s", "t", "u" , "v", "w" ,"x", "y", "z"};                                for(int j = 0; j <n; ++j)                {                    for(int l = 0; l < m; ++l)                    {                        chats[j][l] = scanner.nextInt();                    }                                    }                                int[] noti = new int[n];                int[] msj = new int[m];                                                for(int j = 0; j < k; ++j)                {                    int em =  scanner.nextInt();                    int chat =  scanner.nextInt();                                        ++noti[em-1];                    ++msj[chat-1];                                    }                                for(int i = 0; i < n; ++i){                    int res = 0;                                        for(int j = 0; j < m; ++j)                    {                        if (chats[i][j] != 0)                        {                            res += msj[j];                        }                    }                                        res -= noti[i];                    System.out.print(res + " ");                } 		/*                boolean res = false;                                String s = res ? "YES" : "nO";                */            }} 
