import java.util.*;import java.io.*;import static java.lang.System.out;public class sol_413B {	public static void main(String[] args) {		Scanner sc = new Scanner(System.in);		int n = sc.nextInt();		int m = sc.nextInt();		int k = sc.nextInt();		LinkedList<Integer>[] map = new LinkedList[n];		int[] groups = new int[m];		int[] send = new int[n];		for(int i=0;i<n;i++)		{			map[i] = new LinkedList<Integer>();			for(int j=0;j<m;j++)				if(sc.nextInt()==1)					map[i].add(j);		}		int[] sol = new int[n];		for(int i=0;i<k;i++)		{			int e = sc.nextInt();			int me = sc.nextInt();			e--;			me--;			groups[me]++;			send[e]++;		}		for(int i=0;i<n;i++)		{			int s = 0;			for(int x:map[i])			{				s+= groups[x];			}			s-= send[i];			System.out.print(s+" ");		}		System.out.println();	}}
